mapper.select:
SELECT kwQuery
FROM
T_USER_SEARCH_HISTORY

    userId = #{userId}
    AND searchType = #{searchType.code}


    ORDER BY id DESC
    LIMIT #{topk}

mapper.delete:
delete
from T_USER_SEARCH_HISTORY

    userId = #{userId} AND searchType=#{searchType.code}

delete
from T_USER_SEARCH_HISTORY

    userId = #{userId} AND searchType=#{searchType.code} AND kwQuery=#{kwQuery}
